using System;
using NUnit.Framework;

namespace WikipediaScraper.Test
{
    [TestFixture]
    public class WhenScrapingWikipediaHomePageDidYouKnowSection
    {
        [Test]
        public void HTMLContainsDotDotDotThat()
        {
            //Arrange
            string scrapedHTML = String.Empty;
            string xpath = "//*[@id=\"mp-dyk\"]";
            var scraper = new Scraper();
            var request = new ScrapeRequest
            {
                HTML = TestWikipediaHTMLGenerator.GenerateHTML(),
                XPath = xpath
            };

            //Act
            scrapedHTML = scraper.Scrape(request);

            //Assert
            StringAssert.Contains("... that", scrapedHTML);
        }

        [Test]
        public void HTMLContainsUnorderedListTag()
        {
            //Arrange
            string unorderedListTag = "<ul>";
            string scrapedHTML = String.Empty;
            string xpath = "//*[@id=\"mp-dyk\"]";
            var scraper = new Scraper();
            var request = new ScrapeRequest
            {
                HTML = TestWikipediaHTMLGenerator.GenerateHTML(),
                XPath = xpath
            };

            //Act
            scrapedHTML = scraper.Scrape(request);

            //Assert
            StringAssert.Contains(unorderedListTag, scrapedHTML);
        }
    }

